import Web3 from 'web3';







const { abi } = require('../build/contracts/Retailing.json');
const toAddress = '0x57102EB7596D54cE2C139C7b047024286ca8Ba54';
const tokenContractAddress = '0xa24cc613Ecd1be73B474b12EEb9592388e8509b2'


async function connectWallet() {

    if (window.ethereum && window.ethereum.chainId == '0x61') {
       window.ethereum.request({ method: "eth_requestAccounts" });
       window.web3 = new Web3(window.ethereum);
       const account = web3.eth.accounts;
       const fromAddress = account.givenProvider.selectedAddress;
  //      console.log(`Wallet: ${fromAddress}`);
  //      const contract = new web3.eth.Contract(abi,contractAddress);
  //     console.log(`Contract: ${contract}`);

  //  // const chainId = web3.utils.toHex(97);
  //     const chainId = 97

  //      console.log(`Chain ID: ${chainId}`);
  //      console.log(`abi: ${abi}`);
  //      contract.methods.paid(fromAddress, 11)
  //      .send({
  //            from: fromAddress,
  //            gas: 0x00,
  //            gasPrice: 0x00
  //      }).then(receipt=> {console.log(receipt)})
       
  //   if (typeof window.ethereum !== 'undefined') {
  //     ethereum.request({ method: 'eth_requestAccounts' });
  // } else {
  //     alert('Please install metamask')
  // };


  var Tx = require("ethereumjs-tx").Transaction
  var amount = web3.utils.toHex(1e16)
  web3.eth.getTransactionCount(fromAddress).then(function(v){console.log(v); count = v})  
// set your private key here, we'll sign the transaction below
  var privateKey = "password"  
  var contract = new web3.eth.Contract(abi, tokenContractAddress, {from: fromAddress})
  var rawTransaction = {"from":fromAddress, "gasPrice":web3.utils.toHex(2 * 1e9),"gasLimit":web3.utils.toHex(210000),"to":tokenContractAddress,"value":"0x0","data":contract.methods.paid(toAddress, amount).encodeABI(),"nonce":web3.utils.toHex(10)} 
  }
  var transaction = new Tx(rawTransaction)
  transaction.sign(privateKey)
  web3.eth.sendSignedTransaction('0x' + transaction.serialize().toString('hex'))
  contract.methods.balanceOf(fromAddress).call().then(function(balance){console.log(balance)})
}




  


export default connectWallet
