import Web3 from 'web3';


import Head from 'next/head'
import ListItem from '../../components/ListItem'
import { titleIfy, slugify } from '../../utils/helpers'
import fetchCategories from '../../utils/categoryProvider'
import inventoryForCategory from '../../utils/inventoryForCategory'
import CartLink from '../../components/CartLink'







const { abi } = require('../build/contracts/Retailing.json');
const contractAddress = '0xa24cc613Ecd1be73B474b12EEb9592388e8509b2';
const reciever = '0x57102EB7596D54cE2C139C7b047024286ca8Ba54';


<div className="">
  {cart.map((item, index) => {
    return (
      <div className="border-b py-10" key={index}>
        <div className="flex items-center">
          <Image
            className="w-32 m-0"
            src={item.image}
            alt={item.name}
          />
          <p className="m-0 pl-10 text-gray-600">
            {item.name}
          </p>
          <div className="flex flex-1 justify-end">
            <p className="m-0 pl-10 text-gray-900 font-semibold">
              {DENOMINATION + item.price}
            </p>
          </div>
        </div>
      </div>
    )
  })}
</div>
async function connectWallet() {

    if (window.ethereum && window.ethereum.chainId == '0x61') {
       window.ethereum.request({ method: "eth_requestAccounts" });
       window.web3 = new Web3(window.ethereum);
       const account = web3.eth.accounts;
       const walletAddress = account.givenProvider.selectedAddress;
       console.log(`Wallet: ${walletAddress}`);
       const contract = new web3.eth.Contract(abi,contractAddress);
       const chainId = web3.utils.toHex(97);
       console.log(`Chain ID: ${chainId}`);
       console.log(abi)
       contract.methods.paid(walletAddress)
       .send({
             from: walletAddress,
             gas: 0x00,
             gasPrice: 0x00
       }).then(receipt=> {console.log(receipt)})
       
    if (typeof window.ethereum !== 'undefined') {
      ethereum.request({ method: 'eth_requestAccounts' });
  } else {
      alert('Please install metamask')
  };
  }

}



  


export default connectWallet
